import axios from './api'

const ArticleService = {
	async getArticles() {
		const {data} = await axios.get('/article')
		return data
	},
	async getArticleDetail(slug) {
		const {data} = await axios.get(`/article/${slug}`)
		return data
	},
	async postArticle(article) {
		const {data} = await axios.post('/article/create', {article})
		return data
	},
	async deleteArticle(slug) {
		const {data} = await axios.delete(`/article/${slug}`)
		return data
	},
	async editArticle(slug, article) {
		const {data} = await axios.put(`/article/update/${slug}`, {article})
		return data
	},
}

export default ArticleService
