import {useEffect, useState} from 'react'
import {Input} from '../ui'
import {useSelector, useDispatch} from 'react-redux'
import {signUserFailure, signUserStart, signUserSuccess} from '../slice/auth'
import AuthService from '../service/auth'
import {ValidationError} from '.'
import {useNavigate} from 'react-router-dom'
import icon from "../Logo.png"

const Register = () => {
	const [name, setName] = useState('')
	const [email, setEmail] = useState('')
	const [password, setPassword] = useState('')
	const dispatch = useDispatch()
	const {isLoading, loggedIn} = useSelector(state => state.auth)
	const navigate = useNavigate()

	const registerHandler = async e => {
		e.preventDefault()
		dispatch(signUserStart())
		const user = {name, email, password}
		try {
			async function postJSON(user) {
				try {
					const response = await fetch("http://localhost:8090/api/v1/auth/registration", {
						method: "POST", // or 'PUT'
						headers: {
							"Content-Type": "application/json",
						},
						body: JSON.stringify(user),
					});

					const result = await response.json();
					console.log("Success:", result);
				} catch (error) {
					console.error("Error:", error);
				}
			}

			postJSON(user);
			navigate('/')
		} catch (error) {
			dispatch(signUserFailure(error.response.data.errors))
		}
	}

	useEffect(() => {
		if (loggedIn) {
			navigate('/')
		}
	}, [loggedIn])

	return (
		<div className='text-center mt-5'>
			<main className='form-signin w-25 m-auto'>
				<form>
					<img className='mb-2' src={icon} alt='' width='72' />
					<h1 className='h3 mb-3 fw-normal'>Please register</h1>
					<ValidationError />

					<Input label={'Fullname'} state={name} setState={setName} />
					<Input label={'Email address'} state={email} setState={setEmail} />
					<Input label={'Password'} type={'password'} state={password} setState={setPassword} />

					<button className='w-100 btn btn-lg btn-primary mt-2' disabled={isLoading} onClick={registerHandler} type='submit'>
						{isLoading ? 'loading...' : 'Register'}
					</button>
				</form>
			</main>
		</div>
	)
}

export default Register
