import { useSelector } from 'react-redux'
import { Input, TextArea } from '../ui'

const ArticleForm = props => {
	const { isLoading } = useSelector(state => state.article)
	const {
		title, setTitle,
		description, setDescription,
		body, setBody,
		isEnableTg, setIsEnableTg,
		isEnableIn, setIsEnableIn,
		isEnableInstag, setIsEnableInstag,
		isEnableWeb, setIsEnableWeb,
		formSubmit
	} = props
	console.log(isEnableIn);

	return (
		<form onSubmit={formSubmit}>
			<Input label={'Title'} state={title} setState={setTitle} />
			<TextArea label={'Description'} state={description} setState={setDescription} />
			<TextArea label={'Body'} state={body} setState={setBody} height={'300px'} />

			{isEnableIn && (
				<button type="button" className='text-center m-1 btn btn-outline-info'>
					<svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-telegram" viewBox="0 0 16 16">
						<path d="M16 8A8 8 0 1 1 0 8a8 8 0 0 1 16 0zM8.287 5.906c-.778.324-2.334.994-4.666 2.01-.378.15-.577.298-.595.442-.03.243.275.339.69.47l.175.055c.408.133.958.288 1.243.294.26.006.549-.1.868-.32 2.179-1.471 3.304-2.214 3.374-2.23.05-.012.12-.026.166.016.047.041.042.12.037.141-.03.129-1.227 1.241-1.846 1.817-.193.18-.33.307-.358.336a8.154 8.154 0 0 1-.188.186c-.38.366-.664.64.015 1.088.327.216.589.393.85.571.284.194.568.387.936.629.093.06.183.125.27.187.331.236.63.448.997.414.214-.02.435-.22.547-.82.265-1.417.786-4.486.906-5.751a1.426 1.426 0 0 0-.013-.315.337.337 0 0 0-.114-.217.526.526 0 0 0-.31-.093c-.3.005-.763.166-2.984 1.09z"></path>
					</svg>
				</button>
			)}
			<button className='w-80 btn btn-primary ' disabled={isLoading} type='submit'>
				{isLoading ? 'Loading...' : 'Create'}
			</button>

			{/* <div className='d-inline-flex mt-2 mt-md-0 ms-md-auto'>
				<Link className='me-3 py-2' href="#" aria-label="Search">
					<svg xmlns="http://www.w3.org/2000/svg" width="20" height="20" fill="none" stroke="currentColor" stroke-linecap="round" stroke-linejoin="round" stroke-width="2" class="mx-3" role="img" viewBox="0 0 24 24"><title>Search</title><circle cx="10.5" cy="10.5" r="7.5"></circle><path d="M21 21l-5.2-5.2"></path></svg>
				</Link>
			</div> */}
		</form>
	)
}

export default ArticleForm
